Get-ChildItem -Path . -Recurse -Directory -Name "node_modules" | ForEach-Object { Remove-Item -Path $_ -Recurse -Force -ErrorAction SilentlyContinue }; Get-ChildItem -Path . -Recurse -File -Name "package-lock.json" | ForEach-Object { Remove-Item -Path $_ -Force -ErrorAction SilentlyContinue }; npm cache clean --force; Write-Host "Clean completed! Run 'npm install --legacy-peer-deps'" -ForegroundColor Green